/* eslint-disable no-undef */
<template>
    <div class="layout" id="pet">
        <div class="layout">
            <h2 class="title_text"><img src="../assets/title2.png" alt="" class="title_icon" /> {{ title }}</h2>
            <div class="layout_grid">
                <div class="layout_grid_news" v-for="(news, index) of newsInfo" :key="index">
                    <a :href="'https://www.ftvnews.com.tw/news/detail/' + news.ID" target="_blank" v-if="index <= 3">
                        <div class="grid_cover">
                            <img :src="news.Image" />
                        </div>
                        <div class="grid_info">
                            <h4>{{ news.Title }}</h4>
                            <p class="time">{{ news.CreateDate }}</p>
                        </div>
                    </a>
                </div>
            </div>
        </div>
        <span id="dots"></span
        ><span id="more2">
            <div class="layout" style="margin-top: -160px">
                <div class="layout_grid">
                    <div class="layout_grid_news" v-for="(news, index) of newsInfo" :key="index">
                        <a :href="'https://www.ftvnews.com.tw/news/detail/' + news.ID" target="_blank" v-if="index > 3">
                            <div class="grid_cover">
                                <img :src="news.Image" />
                            </div>
                            <div class="grid_info">
                                <h4>{{ news.Title }}</h4>
                                <p class="time">{{ news.CreateDate }}</p>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        </span>
        <button v-on:click="myFunction" id="myBtn2">閱讀<br />更多</button>
    </div>
</template>
<script>
export default {
    name: 'Intro',
    data() {
        return {
            newsInfo: [],
            title: '齊心抗疫',
        }
    },
    mounted() {
        // eslint-disable-next-line no-undef
        axios
            .get('https://ftvnews-api2.azurewebsites.net/API/FtvGetNewsWeb.aspx?Cate=齊心抗疫&Page=1&sp=20')
            .then((response) => {
                let data = response.data.ITEM
                data.forEach((item) => {
                    this.newsInfo.push(item)
                })
            })
            .catch((error) => {
                console.log('error' + error)
            })
    },
    methods: {
        test: function () {
            this.getElementByClass('readmore').toggle('show')
        },

        myFunction: function () {
            var dots = document.getElementById('dots')
            var moreText = document.getElementById('more2')
            var btnText = document.getElementById('myBtn2')

            if (dots.style.display === 'none') {
                dots.style.display = 'inline'
                btnText.innerHTML = '閱讀<br>更多'
                moreText.style.display = 'none'
            } else {
                dots.style.display = 'none'
                btnText.innerHTML = '回到<br>官網'
                moreText.style.display = 'inline'
            }
        },
    },
}
</script>

<style scoped>
#more2 {
    display: none;
}

.layout {
    background-color: var(--layout_background);
}

.layout_grid {
    width: 1200px;
    border-radius: 5px;
    grid-template-columns: 1fr 1fr 1fr 1fr;
}
.layout_grid_news img {
    width: 100%;
    height: 180px;
    border-radius: 5px 5px 0px 0px;
}

@media screen and (max-width: 1024px) {
    .layout_grid {
        width: 750px;
        grid-template-columns: 1fr 1fr;
    }

    .layout_grid_news img {
        width: 100%;
        height: 190px;
        border-radius: 5px 5px 0px 0px;
    }
}

@media screen and (max-width: 768px) {
    .layout_grid {
        width: 390px;
        grid-template-columns: 1fr;
    }
}

.layout_grid_news {
    box-shadow: 1px 2px 1px #bcb9b9;
    background-color: white;
    border-radius: 5px;
}

.layout_grid_news .grid_info h4 {
    background-color: white;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    white-space: normal;
    max-height: 80px;
    line-height: 2.3rem;
    padding: 10px 1rem 25px;
    font-size: 20px;
    text-decoration: none;
}

.layout_grid_news .grid_info {
    margin: 0;
    color: #0a0a09;
}
max-height: 55px;
.layout_grid_news .grid_info p {
    margin: 0;
    color: #0a0a0a;
}

.layout_grid_news .grid_cover img {
    width: 100%;
}

.layout_grid_news .grid_cover {
    max-height: 400px;
    overflow: hidden;
}

a {
    color: black;
}

p {
    text-align: right;
    padding-right: 1rem;
}

.title_icon {
    margin-bottom: 15px;
    height: var(--title_icon);
}

.title_text {
    color: var(--title_color);
    font-size: var(--font_title);
}

button {
    margin-top: 0px;
    font-size: 20px;
    border-radius: 100%;
    background-color: rgb(243, 191, 199);
    border: transparent 1px solid;
    padding: 1.5rem 2rem;
    margin-bottom: 2rem;
}

.title_text {
    margin-bottom: 0px;
    color: var(--title_color);
    font-size: var(--font_title);
}

h4 {
    font-size: 20px;
}

.time {
    color: #9a9898;
    font-size: 18px;
}
</style>
