/* eslint-disable no-undef */
<template>
    <div class="layout Slick" id="part_news">
        <h2><img src="../assets/title1.png" alt="" class="title_icon" /> {{ title }}</h2>
        <div class="layout_grid">
            <slick class="layout_grid_news" v-for="(news, index) of newsInfo" :key="index">
                <a :href="'https://www.ftvnews.com.tw/news/detail/' + news.ID" target="_blank">
                    <a v-if="index <= 3">
                        <div class="grid_cover">
                            <img :src="news.Image" />
                        </div>
                        <div class="grid_info">
                            <h4>{{ news.Title }}</h4>
                            <p>{{ news.Title }}</p>
                        </div>
                    </a>
                </a>
            </slick>
        </div>
    </div>

</template>

<script>
import slick from 'vue-slick'
export default {
    components: { slick },
    name: 'Intro',
    data() {
        return {
            newsInfo: [],
            title: '編輯精選',
        }
    },
    mounted() {
        // eslint-disable-next-line no-undef
        axios
            .get('https://ftvnews-api2.azurewebsites.net/API/FtvGetNewsWeb.aspx?Cate=暖心故事&Page=1&sp=1')
            .then((response) => {
                let data = response.data.ITEM
                data.forEach((item) => {
                    this.newsInfo.push(item)
                })
            })
            .catch((error) => {
                console.log('error' + error)
            })
    },
    methods: {
        test: function () {
            this.getElementByClass('readmore').toggle('show')
        },
        next() {
            this.$refs.slick.next()
        },

        prev() {
            this.$refs.slick.prev()
        },

        reInit() {
            // Helpful if you have to deal with v-for to update dynamic lists
            this.$nextTick(() => {
                this.$refs.slick.reSlick()
            })
        },

        // Events listeners
        handleAfterChange(event, slick, currentSlide) {
            console.log('handleAfterChange', event, slick, currentSlide)
        },
        handleBeforeChange(event, slick, currentSlide, nextSlide) {
            console.log('handleBeforeChange', event, slick, currentSlide, nextSlide)
        },
        handleBreakpoint(event, slick, breakpoint) {
            console.log('handleBreakpoint', event, slick, breakpoint)
        },
        handleDestroy(event, slick) {
            console.log('handleDestroy', event, slick)
        },
        handleEdge(event, slick, direction) {
            console.log('handleEdge', event, slick, direction)
        },
        handleInit(event, slick) {
            console.log('handleInit', event, slick)
        },
        handleReInit(event, slick) {
            console.log('handleReInit', event, slick)
        },
        handleSetPosition(event, slick) {
            console.log('handleSetPosition', event, slick)
        },
        handleSwipe(event, slick, direction) {
            console.log('handleSwipe', event, slick, direction)
        },
        handleLazyLoaded(event, slick, image, imageSource) {
            console.log('handleLazyLoaded', event, slick, image, imageSource)
        },
        handleLazyLoadError(event, slick, image, imageSource) {
            console.log('handleLazeLoadError', event, slick, image, imageSource)
        },
    },
}
</script>

<style scoped>
.title_icon {
    margin-bottom: -7px;
    height: 30px;
}
.layout_grid{
display:flex;
margin: auto;
justify-content: center;
}
@media screen and (max-width: 1024px) {
    .layout_grid {
        grid-template-columns: 1fr 1fr;
    }
}

@media screen and (max-width: 768px) {
    .layout_grid {
        grid-template-columns: 1fr;
    }
}

.layout_grid_news img {
    width: 100%;
}

.layout_grid_news {
    box-shadow: 1px 2px 3px #bcb9b9;
}

.layout_grid_news .grid_info h4 {
    background-color: white;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    white-space: normal;
    max-height: 55px;
    line-height: 2.3rem;
    padding: 10px 1rem 25px;
}

.layout_grid_news .grid_info p {
    margin: 0;
    color: #737059;
}

.layout_grid_news .grid_cover img {
    width: 100%;
    filter: saturate(0.3);
    transition: 0.3s;
}

.layout_grid_news .grid_cover {
    max-height: 400px;
    overflow: hidden;
}

a {
    color: black;
    font-size: px;
    line-height: 150%;
}

a:hover .grid_cover img {
    filter: saturate(1);
}

p {
    text-align: right;
    padding-right: 1rem;
}

button {
    padding: 2rem 2.5rem;
    margin: auto;
    margin-top: 2rem;
    border-radius: 100%;
    font-size: 20px;
    background-color: lightpink;
    border: transparent 1px solid;
}

.show {
    display: none;
}
*{
    background-color:#F3EAE7
}
</style>
